function compute(){var t=this.getAttribute("data-operation"),e=document.getElementById("firstno").value,n=document.getElementById("secondno").value,r=calcObject[t](e,n);document.getElementById("result").innerHTML=r}window.addEventListener("load",function(){var t=document.getElementsByClassName("opr");Array.prototype.forEach.apply(t,[function(t){t.addEventListener("click",compute)}])});var calcObject={add:function(t,e){return parseInt(t)+parseInt(e)},subtract:function(t,e){return t-e},multiply:function(t,e){return t*e},divide:function(t,e){return t/e}};